# ENSURE adjusted_amount FUNCTION IS PRESENT "
pgr = ActiveRecord::Base.connection.execute <<-SQL
  CREATE OR REPLACE FUNCTION adjusted_amount(op text, amount real) RETURNS REAL AS $$
    BEGIN
      CASE op
        WHEN '-' THEN
          RETURN -1 * amount;
        WHEN '0' THEN
          RETURN 0;
        ELSE
          RETURN amount;
      END CASE;
    END;
  $$ LANGUAGE plpgsql;
SQL